import { ListAnalysesToolsSchema } from "../types/schemas.js";
import handleError from "./handleError.js";
export default async function getAnalysisTools(api) {
    const response = await api.get("/analyses/tools");
    const analysisToolsParsedResponse = ListAnalysesToolsSchema.safeParse(response.data);
    if (!analysisToolsParsedResponse.success) {
        handleError({
            response,
            error: { message: "Cannot retrieve available analysis tools" }
        });
        return [];
    }
    const tools = analysisToolsParsedResponse.data.tools;
    return tools;
}
