export default function humanDatesDiff(date1, date2) {
    const diffTime = Math.abs(date2.getTime() - date1.getTime());
    const diffAsMinutes = Math.floor(diffTime / (1000 * 60));
    const diffAsHours = diffAsMinutes / 60;
    const diffAsDays = diffAsHours / 24;
    const days = Math.floor(diffAsDays);
    const hours = Math.floor(diffAsHours - days * 24);
    const minutes = Math.floor(diffAsMinutes - hours * 60 - days * 60);
    return days > 0
        ? `${days}d ${hours}h ${minutes}m`
        : hours > 0
            ? `${hours}h ${minutes}m`
            : minutes > 0
                ? `${minutes}m ${Math.floor(diffTime / 1000 - minutes * 60)}s`
                : `${diffTime / 1000}s`;
}
